import { Component } from "react";
import Table from "react-bootstrap/Table";
import Form from "react-bootstrap/Form";
import API from "./utils/API.js";
import Jumbo from "./components/Jumbo.js";
import TableSearch from "./components/TableSearch.js";
import "./App.css";

class App extends Component {

  state = {
    search: "",
    firstName: "",
    lastName: "",
    username: "",
    results: [],
  };
  componentDidMount() {
    API.getUsers()
      .then((res) => {
        console.log(res.data);
        this.setState({ results: res.data.results });
      })
      .catch((err) => console.log(err));
  }


  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    const value = event.target.value;
    const name = event.target.name;

    // Updating the input's state
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = (event) => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();
    // if (!this.state.firstName || !this.state.lastName) {
    //   alert("Fill out your first and last name please!");
    // } else if (this.state.password.length < 6) {
    //   alert(
    //     `Choose a more secure password ${this.state.firstName} ${this.state.lastName}`
    //   );
    // } else {
    //   alert(`Hello ${this.state.firstName} ${this.state.lastName}`);
    // }

    console.log(this.state.firstName);

    this.setState({
      firstName: "",
      lastName: "",
      password: "",
    });
  };










  render() {
    return (
      <div className="App">
        <Jumbo />
        <TableSearch />
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="text" placeholder="Search for a person" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>
        </Form>
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
            </tr>
          </thead>
          <tbody>
            {this.state.results.map((result, i) => (
              <tr>
                <td>{i + 1}</td>
                <td>{result.name.first}</td>
                <td>{result.name.last}</td>
                <td>{result.login.username}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    );
  }
}

export default App;
